University.create([
  {id: 8, name: "Белорусский государственный экономический университет", short_name: "БГЭУ", description: "Белорусский государственный экономический университет - одно из самых крупнейших учебных заведений на территории Республики Беларусь. Одно из самых популярных мест для поступления среди жителей Минска, а так же областей. Данный университет имеет в своем составе 11 факультетов и каждый абитуриент выбравший данное учебное заведение сможет с легкостью определиться с выбором специальности, начиная от банковского дела и заканчивая международной бизнес коммуникацией. Данный университет пользуется своим успехом у иностранных граждан, которые приезжают в РБ с целью получения высшего образования в престижном ВУЗе.", image: "____.jpg", created_at: "2016-06-19 10:12:54", updated_at: "2016-07-31 17:30:25", city_id: 1, address: "г. Минск, Партизанский, 26", preparatory_department: true, hostel: true, rating: 60, magistracy: false, initial_cost: 2000, ochno: true, zaochno: true},
  {id: 5, name: "Белорусский государственный университет", short_name: "БГУ", description: "Белорусский государственный университет (белор. Беларускі дзяржаўны ўніверсітэт) — Ведущий вуз Республики Беларусь расположенный в Минске. Открыт 30 октября 1921 года. Вошел в ТОП-3 ведущих университетов по СНГ на состояние 2015 г. Является гордостью среди образовательных учреждений по Беларуси. Проводит подготовку по специальностям начиная от «Международное право» - до обучения в магистратуре по специальностям «Технологии управления персоналом». Студсоюз данного университета постоянно проводит различные активные мероприятия, которые способствуют тому, что БГУ становится не просто учебным учреждением, но и тем местом, где студенты при желании могут проявить свои личностные качества. Студентам, которые показывают успехи в учебной деятельности получают право посещения спортивным залом, а так же бассейна.", image: "___.jpg", created_at: "2016-06-11 20:28:21", updated_at: "2016-07-31 17:32:20", city_id: 1, address: "г. Минск, пр. Независимости, 4б", preparatory_department: true, hostel: true, rating: 75, magistracy: false, initial_cost: 2300, ochno: true, zaochno: true},
  {id: 10, name: "Гродненский государственный университет имени Янки Купалы", short_name: "ГрГУ", description: "Гродненский государственный университет имени Янки Купалы – одно из старейших высших учебных заведений на территории Республики Беларусь, находится в городе Гродно. Имеет международные сертификаты соответствия системы менеджмента качества требованиям (Серия стандартов ISO 9000). В 2011 году стал первым среди высших учебных заведений Республики Беларусь лауреатом конкурса «Премия Правительства Республики Беларусь за достижения в области качества». Первыми после БГУ ввели системы электронных студенческих билетов. Занимает 2-е место среди лучших университетов Беларуси и 1861 место в мире среди 12.000 университетов.", image: "______.jpg", created_at: "2016-06-29 11:11:47", updated_at: "2016-07-11 18:25:12", city_id: 2, address: "г. Гродно, Ожешко, 22", preparatory_department: true, hostel: true, rating: 10, magistracy: false, initial_cost: 1870, ochno: true, zaochno: true},
  {id: 7, name: "Белорусский государственный университет информатики и радиоэлектроники", short_name: "БГУИР", description: "Белорусский государственный университет информатики и радиоэлектроники – ведущее высшее учебное заведение Беларуси в области компьютерных технологий и электроники. В данном учебном заведении обучается около 15.300 студентов ежегодно и из них около 400 человек из стран ближнего и дальнего зарубежья. Является одним из лучших учебных заведений Республики Беларусь. Выпускники данного ВУЗа нередко становятся успешными программистами, занятые в Парке Высоких Технологий РБ. Данное учебное заведение идет в ногу со временем и часто организовывает международные научные конференции, семинары по инновационным направлениям/технологиям.", image: "_____.jpg", created_at: "2016-06-16 15:54:44", updated_at: "2016-07-31 12:19:13", city_id: 1, address: "г. Минск, Бровки, 6", preparatory_department: true, hostel: true, rating: 80, magistracy: true, initial_cost: 2000, ochno: true, zaochno: true},
  {id: 6, name: "Минский государственный лингвистический университет", short_name: "МГЛУ", description: "Минский государственный лингвистический университет – высшее учебное заведение, находящееся в городе Минске. В данном университете готовят кадров в области педагогики и перевода. Для иностранных студентов данный ВУЗ будет очень привлекательным тем, что МГЛУ является главным центром по подготовке студентов со всех континентов мира. Со дня основания в 1948 году данный лингвистический университет успел подготовить около 25500 учителей и около 2600 переводчиков-референтов. Также данный университет носит звание главного центра по подготовке студентов по иностранным языкам. МГЛУ часто проводит различные международные конференции куда приглашаются специалисты из России, Канады, Бельгии, Германии и др. Каждый студент обязан изучать 2 иностранных языка весь срок обучения, однако при хорошей успеваемости имеет право выбрать и 3-й. Данный университет всячески поощряет углубленное изучение языков и создает все условия для этого.", image: "____.jpg", created_at: "2016-06-11 20:56:08", updated_at: "2016-07-31 17:31:40", city_id: 1, address: "г. Минск, Захарова, 21", preparatory_department: true, hostel: true, rating: 55, magistracy: false, initial_cost: 2200, ochno: true, zaochno: false}
])
